mapper.select:
        SELECT


--         级联表的字段
        ,jingdian.jingdian_name as jingdianName
        ,jingdian.jingdian_photo as jingdianPhoto
        ,jingdian.jingdian_types as jingdianTypes
        ,jingdian.jingdian_money as jingdianMoney
        ,jingdian.jingdian_number as jingdianNumber
        ,jingdian.jingdian_clicknum as jingdianClicknum
        ,jingdian.jingdian_content as jingdianContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.yonghu_delete as yonghuDelete

        FROM jingdian_liuyan  a
        left JOIN jingdian jingdian ON a.jingdian_id = jingdian.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.jingdian_id = #{params.jingdianId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.jingdian_liuyan_text like CONCAT('%',#{params.jingdianLiuyanText},'%')


                and a.reply_text like CONCAT('%',#{params.replyText},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




                and a.jingdian_id IS NOT NULL


                and jingdian.jingdian_name like CONCAT('%',#{params.jingdianName},'%')


                and jingdian.jingdian_types = #{params.jingdianTypes}



                  and jingdian.jingdian_money >= #{params.jingdianMoneyStart}   


                  and jingdian.jingdian_money <= #{params.jingdianMoneyEnd}   


                  and jingdian.jingdian_number >= #{params.jingdianNumberStart}   


                  and jingdian.jingdian_number <= #{params.jingdianNumberEnd}   


                and jingdian.jingdian_number = #{params.jingdianNumber}


                  and jingdian.jingdian_clicknum >= #{params.jingdianClicknumStart}   


                  and jingdian.jingdian_clicknum <= #{params.jingdianClicknumEnd}   


                and jingdian.jingdian_clicknum = #{params.jingdianClicknum}


                and jingdian.jingdian_content like CONCAT('%',#{params.jingdianContent},'%')



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


                and yonghu.yonghu_delete = #{params.yonghuDelete}



        order by a.${params.orderBy} desc
