mapper.select:
        SELECT


--         级联表的字段

        FROM meishi  a



                and a.id in

                #{item}



                and a.meishi_name like CONCAT('%',#{params.meishiName},'%')


                and a.meishi_types = #{params.meishiTypes}


                  and a.meishi_number >= #{params.meishiNumberStart}   


                  and a.meishi_number <= #{params.meishiNumberEnd}   


                and a.meishi_number = #{params.meishiNumber}


                  and a.meishi_money >= #{params.meishiMoneyStart}   


                  and a.meishi_money <= #{params.meishiMoneyEnd}   


                  and a.meishi_clicknum >= #{params.meishiClicknumStart}   


                  and a.meishi_clicknum <= #{params.meishiClicknumEnd}   


                and a.meishi_clicknum = #{params.meishiClicknum}


                and a.meishi_content like CONCAT('%',#{params.meishiContent},'%')




        order by a.${params.orderBy} desc
